import React, { useState } from 'react';
import './styles/searchbar.css'


/* searchbar onChange:
	- update location state (where should the state be?)
	- make api call / sort data
	- change UI (depending where this needs to happen the state might need to
		be lifted	
*/
const SearchBar = () => {
	return (
		<div id="city-form"> 
			<input 
				id="city-search" 
				type="text" 
				placeholder="Search city..."
			/>
		</div>				
	);
}

export default SearchBar;
