import React, {useState} from 'react';
import axios from 'axios';
import './components/phone-frame.css';
import SearchBar from './components/search.js'

function App() {

	const api_key = '3cb9449ad382eea08832734e94a58749';
	const [data, setData] = useState({});	
	const [city, setCity] = useState('');
	const url = `https://api.openweathermap.org/data/2.5/weather`; // missing ?q=

	function buildUrl(city) {
		let finalURL = url + `${city}&appid=${api_key}`;
		return finalURL;
	}

	// Searches for using city parameter
	async function handleSearch(city) {
		// change city state
		setCity(city);
		try {
			// api call 	
//			const response = await axios.get(buildUrl(city));
			const response = await axios.get(url, {
				params: {
					q: city,
					appid: api_key
				}
			});
			setData(response.data);
		} catch (error) {console.error(error);}
		finally	{setCity('');}
	}

  	return (
    	<div className="phone-frame">
			<SearchBar onSearch={handleSearch} onChange={setCity} location={city}/>
			<p>{data.name}</p>

    	</div>
  	);
}

export default App;
