	//}

	return (
		<div id="city-form"> 
			<input 
				id="city-search" 
				type="text" 
				placeholder="Search city..."
				value={location}
				onKeyPress={handleKeyPress}
				onChange={handleChange}
			/>
		</div>				
	);
}

export default SearchBar;
