import React, {useState} from 'react';
import axios from 'axios';
import './components/phone-frame.css';
import SearchBar from './components/search.js'

function App() {

	const api_key = '3cb9449ad382eea08832734e94a58749';
	const [data, setData] = useState({});	
	const [city, setCity] = useState('');
	const url = `https://api.openweathermap.org/data/2.5/weather?q=`;

	function buildUrl(city) {
		let finalURL = url + `${city}&appid=${api_key}`;
		return finalURL;
	}

	// Searches for using city parameter
	async function handleSearch(city) {
		// change city state
		setCity(city);
		// api call 	
		const response = await axios.get(buildUrl(city));
		// change data to call response
		setData(response.data);
		console.log(response.data);
	}

  	return (
    	<div className="phone-frame">
			<SearchBar onSearch={handleSearch} onChange={setCity} location={city}/>
			<p>{city}</p>
    	</div>
  	);
}

export default App;
